import React, { useState } from "react";
import * as pdfjsLib from "pdfjs-dist/build/pdf";
import pdfjsWorker from "pdfjs-dist/build/pdf.worker.min.mjs";

// PDF.js ÏõåÏª§ ÏÑ§Ï†ï (Î≤àÎì§Îêú worker ÏÇ¨Ïö©)
pdfjsLib.GlobalWorkerOptions.workerSrc = pdfjsWorker;
  function App() {
  const [pdfFile, setPdfFile] = useState(null);
  const [extractedText, setExtractedText] = useState("");
  const [mode, setMode] = useState("all");
  const [loading, setLoading] = useState(false);
  const [summary, setSummary] = useState("");
  const [quiz, setQuiz] = useState("");
  const [assignments, setAssignments] = useState("");
  const [error, setError] = useState("");

  // PDF ÌååÏùº ÏÑ†ÌÉù
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setPdfFile(file || null);
    setExtractedText("");
    setSummary("");
    setQuiz("");
    setAssignments("");
    setError("");
  };

  // PDFÏóêÏÑú ÌÖçÏä§Ìä∏ Ï∂îÏ∂ú
  const handleExtractText = async () => {
    if (!pdfFile) {
      alert("Î®ºÏ†Ä PDF ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }

    try {
      setLoading(true);
      setError("");
      setExtractedText("");

      const arrayBuffer = await pdfFile.arrayBuffer();
      const pdf = await pdfjsLib.getDocument({ data: arrayBuffer }).promise;

      let fullText = "";
      for (let pageNum = 1; pageNum <= pdf.numPages; pageNum++) {
        const page = await pdf.getPage(pageNum);
        const content = await page.getTextContent();
        const strings = content.items.map((item) => item.str);
        fullText += strings.join(" ") + "\n\n";
      }

      setExtractedText(fullText);
    } catch (err) {
      console.error(err);
      setError("PDF ÌÖçÏä§Ìä∏ Ï∂îÏ∂ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  // Î∞±ÏóîÎìú /api/generate Ìò∏Ï∂ú
  const handleGenerate = async () => {
    if (!extractedText.trim()) {
      alert("Î®ºÏ†Ä PDFÏóêÏÑú ÌÖçÏä§Ìä∏Î•º Ï∂îÏ∂úÌïòÍ±∞ÎÇò, ÌÖçÏä§Ìä∏ ÏòÅÏó≠Ïóê ÎÇ¥Ïö©ÏùÑ ÏßÅÏ†ë ÏûÖÎ†•Ìï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }

    setLoading(true);
    setError("");
    setSummary("");
    setQuiz("");
    setAssignments("");

    try {
      const res = await fetch("http://localhost:8000/api/generate", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          text: extractedText,
          mode: mode,
        }),
      });

      if (!res.ok) {
        const data = await res.json().catch(() => ({}));
        throw new Error(data.detail || "ÏöîÏ≤≠ Ïã§Ìå®");
      }

      const data = await res.json();
      setSummary(data.summary || "");
      setQuiz(data.quiz || "");
      setAssignments(data.assignments || "");
    } catch (err) {
      console.error(err);
      setError(err.message || "ÏöîÏ≤≠ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-root">
      <header className="app-header">
        <div>
          <h1>Upstage PDF ‚Üí ÏöîÏïΩ ¬∑ ÌÄ¥Ï¶à ¬∑ Í≥ºÏ†ú ÏÉùÏÑ±Í∏∞</h1>
          <p className="subtitle">
            ÏàòÏóÖÏûêÎ£å PDFÎ•º Ïò¨Î¶¨Î©¥, Î∏åÎùºÏö∞Ï†ÄÏóêÏÑú ÌÖçÏä§Ìä∏Î•º ÎΩëÏïÑÏÑú Upstage SolarÎ°ú
            ÏöîÏïΩ/ÌÄ¥Ï¶à/Í≥ºÏ†úÎ•º Ìïú Î≤àÏóê ÎßåÎì§Ïñ¥ Ï§çÎãàÎã§.
          </p>
        </div>
        <span className="badge">v1 ¬∑ Demo</span>
      </header>

      <main className="app-main">
        {/* ÏôºÏ™Ω: ÏóÖÎ°úÎìú + ÏòµÏÖò + ÏõêÎ¨∏ */}
        <section className="panel panel-left">
          <div className="card">
            <h2>1. PDF ÏóÖÎ°úÎìú</h2>
            <p className="hint">
              Í∞ïÏùò Ïä¨ÎùºÏù¥Îìú / ÏàòÏóÖÏûêÎ£å PDF ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥ Ï£ºÏÑ∏Ïöî.
            </p>
            <div className="file-row">
              <label className="file-label">
                <span>PDF ÏÑ†ÌÉù</span>
                <input
                  type="file"
                  accept="application/pdf"
                  onChange={handleFileChange}
                />
              </label>
              {pdfFile && (
                <span className="file-name">üìÑ {pdfFile.name}</span>
              )}
            </div>
            <button
              className="primary-btn"
              onClick={handleExtractText}
              disabled={loading || !pdfFile}
            >
              {loading ? "Ï≤òÎ¶¨ Ï§ë..." : "PDFÏóêÏÑú ÌÖçÏä§Ìä∏ Ï∂îÏ∂ú"}
            </button>
          </div>

          <div className="card">
            <h2>2. Ï∂îÏ∂úÎêú ÌÖçÏä§Ìä∏</h2>
            <p className="hint">
              ÌïÑÏöîÌïòÎã§Î©¥ ÏïÑÎûò ÎÇ¥Ïö©ÏùÑ Ï°∞Í∏à Í≥†Ïπú Îí§ Í∑∏ÎåÄÎ°ú ÏÇ¨Ïö©Ìï¥ÎèÑ Îê©ÎãàÎã§.
            </p>
            <textarea
              className="text-area"
              rows={12}
